            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'ID_SEWAAN' => $this->input->post('ID_SEWAAN'),
					'ID_USER' => $this->input->post('ID_USER'),
					'TGL_TRANSAKSI' => $this->input->post('TGL_TRANSAKSI'),
					'LAMA_SEWA' => $this->input->post('LAMA_SEWA'),
					'TOTAL_TARIF' => $this->input->post('TOTAL_TARIF'),
                );

                $this->Transaksi_sewa_model->update_transaksi_sewa($ID_TRAKSAKSI,$params);            
                redirect('transaksi_sewa/index');
            }
            else
            {
                $data['_view'] = 'transaksi_sewa/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The transaksi_sewa you are trying to edit does not exist.');
    } 

    /*
     * Deleting transaksi_sewa
     */
    function remove($ID_TRAKSAKSI)
    {
        $transaksi_sewa = $this->Transaksi_sewa_model->get_transaksi_sewa($ID_TRAKSAKSI);

        // check if the transaksi_sewa exists before trying to delete it
        if(isset($transaksi_sewa['ID_TRAKSAKSI']))
        {
            $this->Transaksi_sewa_model->delete_transaksi_sewa($ID_TRAKSAKSI);
            redirect('transaksi_sewa/index');
